import jsonimport sysfrom os.path import isfile, joindef main():    filename = join("terraform", 'plan.json')    print(filename)    try:        with open(filename) as j:            obj = json.load(j)            new_repos = find_new_repos(obj['resource_changes'])            print(new_repos)            if len(new_repos) > 1:                sys.exit(1)            else:                if len(new_repos) == 1:                    print(new_repos[0])                # Successful exit code on 0 new repos, ie deletes                sys.exit(0)    except json.JSONDecodeError as error:        print("Error parsing json file {}".format(error.msg))        sys.exit(1)def find_new_repos(change_list):    new_repos = []    for repo in change_list:        if len(repo['change']['actions']) == 1 and repo['change']['actions'][0] == 'create':            new_repos.append(repo['change']['after']['name'])        return new_reposif __name__ == "__main__":    main()